mapper.select:
SELECT mno, id, name, type, birth, tell, status, confirm, grade, role, joindate, finaldate
FROM member
WHERE id = #{id}

SELECT tell
FROM member
WHERE tell = #{tell}

SELECT mno, id, name, type, birth, tell, status, confirm, grade, role, joindate, finaldate
FROM member
WHERE id = #{id}
AND pwd = #{pwd}

mapper.update:
UPDATE member
SET finaldate = now()
WHERE id = #{id}

UPDATE member
SET role = #{role}
WHERE id = #{id}

UPDATE member
SET grade = #{grade}
WHERE id = #{id}

UPDATE member
SET confirm = 1
WHERE id = #{id}
