mapper.select:
SELECT mno, id, name, type, birth, tell, status, confirm, grade, role, joindate, finaldate
FROM member
WHERE id = #{id}

SELECT tell
FROM member
WHERE tell = #{tell}

SELECT mno, id, name, type, birth, tell, status, confirm, grade, role, joindate, finaldate FROM member
WHERE id = #{id}
AND pwd = #{pwd}

SELECT m.* 
   FROM (SELECT @rownum:=@rownum+1 AS rnum, tmp.* FROM MEMBERLISTVIEW AS tmp,
           (SELECT @rownum:=0) as r



                WHERE tmp.id LIKE '%'||#{word}||'%'


            WHERE ${key} = #{word}



      ) AS m
   WHERE rnum < = #{end}
   AND rnum > = ${start}                
   ORDER BY m.mno DESC

mapper.update:
UPDATE member
SET finaldate = now()
WHERE id = #{id}

UPDATE member
SET role = #{role}
WHERE id = #{id}

UPDATE member
SET grade = #{grade}
WHERE id = #{id}

UPDATE member
SET confirm = 1
WHERE id = #{id}
